insert.foreach:
INSERT INTO we_flower_customer_rel (
id, user_id, external_userid, oper_userid, remark, description, create_time, remark_corp_name, remark_mobiles, qq, corp_id, address, email, add_way, state, status,
) VALUES (
#{item.id}, #{item.userId}, #{item.externalUserid}, #{item.operUserid}, #{item.remark}, #{item.description}, #{item.createTime}, #{item.remarkCorpName}, #{item.remarkMobiles}, #{item.qq}, #{item.corpId}, #{item.address}, #{item.email}, #{item.addWay}, #{item.state}, #{item.status},
)ON DUPLICATE KEY UPDATE
id=#{item.id}, user_id=#{item.userId}, external_userid=#{item.externalUserid}, oper_userid=#{item.operUserid}, remark=#{item.remark}, description=#{item.description}, create_time=#{item.createTime}, remark_corp_name=#{item.remarkCorpName}, remark_mobiles=#{item.remarkMobiles}, qq=#{item.qq}, corp_id = #{item.corpId}, address=#{item.address}, email=#{item.email}, add_way=#{item.addWay}, state=#{item.state}, status=#{item.status},

mapper.update:
UPDATE we_flower_customer_rel
SET id          = #{takeoverRelId},
    user_id     = #{takeoverUserid},
    create_time = now()
WHERE corp_id = #{corpId}
  AND id = #{handoverRelId}

UPDATE we_flower_customer_rel
SET status = '${@com.easyink.common.enums.CustomerStatusEnum@NORMAL.code}'
WHERE corp_id = #{corpId}
  AND user_id = #{userId}
  AND external_userid = #{external_userid}

mapper.select:
select
left(wfcr.create_time,10) as createTime ,
ifnull(count(*),0) as total
from
we_flower_customer_rel wfcr

    wfcr.corp_id = #{corpId}
    and wfcr.state = #{state}
    and wfcr.add_way = #{addWay}

        AND wfcr.create_time >= #{beginTime}


        AND wfcr.create_time <= #{endTime}


group by
left(wfcr.create_time,10)

SELECT
        IFNULL((SELECT COUNT(id)
            FROM we_flower_customer_rel
            WHERE
                corp_id = #{corpId}
                AND user_id = #{userId}
                AND status = '${@com.easyink.common.enums.CustomerStatusEnum@DRAIN.code}'

                    AND create_time >= #{beginTime}


                    AND create_time <= #{endTime}

                ), 0) AS newCustomerLossCnt,
        IFNULL((SELECT COUNT(id)
            FROM we_flower_customer_rel
            WHERE
                corp_id = #{corpId}
                AND user_id = #{userId}
                AND status NOT IN ('${@com.easyink.common.enums.CustomerStatusEnum@DRAIN.code}', '${@com.easyink.common.enums.CustomerStatusEnum@DELETE.code}')
                AND create_time <= #{endTime}
                ), 0) AS contactTotalCnt

SELECT wc.`external_userid`, wc.`name` AS userName, wc.`corp_id`, wc.`unionid` as unionId, wfcr.id , wfcr.`user_id`, wfcr.`status` FROM we_customer wc LEFT JOIN we_flower_customer_rel wfcr ON wc.corp_id = wfcr.corp_id AND wc.external_userid = wfcr.external_userid WHERE wc.corp_id = #{corpId} AND wc.unionid IN
#{unionId}
AND wfcr.status IN ('${@com.easyink.common.enums.CustomerStatusEnum@NORMAL.code}', '${@com.easyink.common.enums.CustomerStatusEnum@DRAIN.code}')

SELECT wfcr.external_userid, wfcr.user_id, wfcr.corp_id, wfcr.remark_mobiles, wfcr. STATUS, wfcr.id FROM we_flower_customer_rel wfcr WHERE wfcr.corp_id = #{corpId} AND wfcr.remark_mobiles IN
#{mobile}
AND wfcr.status IN ('${@com.easyink.common.enums.CustomerStatusEnum@NORMAL.code}', '${@com.easyink.common.enums.CustomerStatusEnum@DRAIN.code}')

SELECT user_id AS userId, COUNT(external_userid) AS currentNewCustomerCnt
FROM we_flower_customer_rel
WHERE corp_id = #{corpId}
AND `status` IN ('${@com.easyink.common.enums.CustomerStatusEnum@NORMAL.code}',
'${@com.easyink.common.enums.CustomerStatusEnum@TO_BE_TRANSFERRED.code}',
'${@com.easyink.common.enums.CustomerStatusEnum@TRANSFERRING.code}')

    AND `create_time` >= #{beginTime}


    AND `create_time` <= #{endTime}


    AND user_id IN

        #{userId}


GROUP BY user_id

SELECT DATE_FORMAT(wfcr.create_time, '%Y-%m-%d') AS xTime, COUNT(wfcr.external_userid) AS currentNewCustomerCnt
FROM we_flower_customer_rel wfcr
LEFT JOIN we_user wu ON wu.user_id = wfcr.user_id AND wu.corp_id = wfcr.corp_id
LEFT JOIN we_department wd ON wd.id = wu.main_department AND wd.corp_id = wu.corp_id
WHERE wfcr.corp_id = #{corpId}
${params.dataScope}
AND wu.is_activate = ${@com.easyink.common.constant.WeConstans@WE_USER_IS_ACTIVATE}
AND wfcr.`status` IN ('${@com.easyink.common.enums.CustomerStatusEnum@NORMAL.code}',
'${@com.easyink.common.enums.CustomerStatusEnum@TO_BE_TRANSFERRED.code}',
'${@com.easyink.common.enums.CustomerStatusEnum@TRANSFERRING.code}')

    AND wfcr.`create_time` >= #{beginTime}


    AND wfcr.`create_time` <= #{endTime}


    AND wfcr.user_id IN

        #{userId}


GROUP BY xTime

SELECT COUNT(wfcr.external_userid) AS currentNewCustomerCnt
FROM we_flower_customer_rel wfcr
LEFT JOIN we_user wu ON wu.user_id = wfcr.user_id AND wu.corp_id = wfcr.corp_id
LEFT JOIN we_department wd ON wd.id = wu.main_department AND wd.corp_id = wu.corp_id
WHERE wfcr.corp_id = #{corpId}
${params.dataScope}
AND wu.is_activate = ${@com.easyink.common.constant.WeConstans@WE_USER_IS_ACTIVATE}
AND wfcr.`status` IN ('${@com.easyink.common.enums.CustomerStatusEnum@NORMAL.code}',
'${@com.easyink.common.enums.CustomerStatusEnum@TO_BE_TRANSFERRED.code}',
'${@com.easyink.common.enums.CustomerStatusEnum@TRANSFERRING.code}')

    AND wfcr.`create_time` >= #{beginTime}


    AND wfcr.`create_time` <= #{endTime}


    AND wfcr.user_id IN

        #{userId}

SELECT SUM(CASE WHEN `type` = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@ADD.type}' THEN 1 ELSE 0 END) AS customerCnt,
       channel_id AS `state`
FROM we_emple_code_analyse WHERE corp_id = #{corpId}


        AND user_id IN

            #{item}



        AND user_id = #{userIds}



    AND channel_id IN

        #{channelId}



    AND time <= DATE_FORMAT(#{endTime}, '%Y-%m-%d')

GROUP BY `state`

SELECT SUM(CASE WHEN `status` IN ('${@com.easyink.common.enums.CustomerStatusEnum@NORMAL.code}',
'${@com.easyink.common.enums.CustomerStatusEnum@TO_BE_TRANSFERRED.code}',
'${@com.easyink.common.enums.CustomerStatusEnum@TRANSFERRING.code}' ) THEN 1 ELSE 0 END) AS customerCnt, state
FROM we_flower_customer_rel WHERE corp_id = #{corpId}


        AND user_id IN

            #{item}



        AND user_id = #{userIds}



    AND state IN

        #{state}



    AND create_time >= #{beginTime}


    AND create_time <= #{endTime}

GROUP BY state

SELECT SUM(CASE WHEN `type` = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@ADD.type}' THEN 1 ELSE 0 END) AS customerCnt,
       `time` AS `date`
FROM we_emple_code_analyse WHERE corp_id = #{corpId}


        AND user_id IN

            #{item}



        AND user_id = #{userIds}



    AND channel_id IN

        #{channelId}



    AND `time` <= DATE_FORMAT(#{endTime}, '%Y-%m-%d')

GROUP BY `date`

SELECT SUM(CASE WHEN `status` IN ('${@com.easyink.common.enums.CustomerStatusEnum@NORMAL.code}',
'${@com.easyink.common.enums.CustomerStatusEnum@TO_BE_TRANSFERRED.code}',
'${@com.easyink.common.enums.CustomerStatusEnum@TRANSFERRING.code}' ) THEN 1 ELSE 0 END) AS customerCnt, DATE_FORMAT(create_time, '%Y-%m-%d') AS `date`
FROM we_flower_customer_rel WHERE corp_id = #{corpId}


        AND user_id IN

            #{item}



        AND user_id = #{userIds}



    AND state IN

        #{state}



    AND create_time >= #{beginTime}


    AND create_time <= #{endTime}

GROUP BY `date`

SELECT COUNT(external_userid) FROM we_flower_customer_rel
WHERE corp_id = #{corpId}
      AND status != '${@com.easyink.common.enums.CustomerStatusEnum@DELETE.code}'

    AND create_time <= #{endTime}


    AND user_id IN

        #{userId}

SELECT COUNT(external_userid) FROM we_flower_customer_rel
WHERE corp_id = #{corpId}
AND status = '${@com.easyink.common.enums.CustomerStatusEnum@TO_BE_TRANSFERRED.code}'

    AND create_time <= #{endTime}


    AND user_id IN

        #{userId}

SELECT user_id, corp_id, COUNT(external_userid) AS totalAllContactCnt
FROM we_flower_customer_rel
WHERE corp_id = #{corpId}
AND status != '${@com.easyink.common.enums.CustomerStatusEnum@DELETE.code}'

    AND create_time <= #{endTime}


    AND user_id IN

        #{userId}


GROUP BY user_id

SELECT external_userid, create_time FROM we_flower_customer_rel WHERE user_id IN ( SELECT user_id FROM we_user wu INNER JOIN we_department wd ON wu.corp_id = wd.corp_id AND wd.id = wu.main_department AND wu.is_activate = ${@com.easyink.common.constant.WeConstans@WE_USER_IS_ACTIVATE} ${rootEntity.params.dataScope})
AND external_userid IN
#{externalUserid}
AND corp_id = #{corpId} AND status IN ('${@com.easyink.common.enums.CustomerStatusEnum@NORMAL.code}', '${@com.easyink.common.enums.CustomerStatusEnum@DRAIN.code}', '${@com.easyink.common.enums.CustomerStatusEnum@TO_BE_TRANSFERRED.code}', '${@com.easyink.common.enums.CustomerStatusEnum@TRANSFERRING.code}') ORDER BY `create_time` DESC LIMIT #{pageNum}, #{pageSize}

SELECT DATE_FORMAT(wfcr.create_time, '%Y-%m-%d') AS xTime, COUNT(wfcr.external_userid) AS currentNewCustomerCnt
FROM we_flower_customer_rel wfcr
LEFT JOIN we_user wu ON wu.user_id = wfcr.user_id AND wu.corp_id = wfcr.corp_id
LEFT JOIN we_department wd ON wd.id = wu.main_department AND wd.corp_id = wu.corp_id
WHERE wfcr.corp_id = #{corpId}
AND wu.is_activate = ${@com.easyink.common.constant.WeConstans@WE_USER_IS_ACTIVATE}
AND wfcr.`status` IN ('${@com.easyink.common.enums.CustomerStatusEnum@NORMAL.code}',
'${@com.easyink.common.enums.CustomerStatusEnum@TO_BE_TRANSFERRED.code}',
'${@com.easyink.common.enums.CustomerStatusEnum@TRANSFERRING.code}')

    AND wfcr.`create_time` >= #{beginTime}


    AND wfcr.`create_time` <= #{endTime}


    AND wfcr.user_id IN

        #{userId}


GROUP BY xTime
